    void buscarpedido(Object fecha)
    {
        String titulos[]={"CODIGO","CANTIDAD DE PRODUCTOS","FECHA"};
        DefaultTableModel model= new DefaultTableModel(null,titulos);
        jTable1.setModel(model);
        conectar cc=new conectar();
        Connection cn= cc.conexion();
        String sql ="";
        Object datos[]= new Object[3];
    
        if(fecha.equals(""))
        {
            sql= "SELECT * FROM pedidos";
        }
        else
        {
            sql="SELECT * FROM pedidos WHERE fecped='"+fecha+"'";
        }
        try {
            Statement st= cn.createStatement();
            ResultSet rs = st.executeQuery(sql);
            while(rs.next())
            {
                datos[0]= rs.getString(1);
                datos[1]= rs.getInt(2);
                datos[2]= rs.getDate(3);
                model.addRow(datos);
                
            }
        } catch (SQLException ex) {
            Logger.getLogger(frmpedidos.class.getName()).log(Level.SEVERE, null, ex);
        }
    
    }

