private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
// TODO add your handling code here:
    conectar cc= new conectar();
    Connection cn = cc.conexion();
        try {
            PreparedStatement pst = cn.prepareStatement("INSERT INTO pedidos(codped,cantped,fecped) VALUES (?,?,?)");
            pst.setString(1, jTextField1.getText());
            pst.setInt(2, Integer.parseInt(jTextField2.getText()));
            pst.setDate(3, new java.sql.Date(jDateChooser1.getDate().getTime()));
            pst.executeUpdate();
            buscarpedido("");
        } catch (SQLException ex) {
            Logger.getLogger(frmpedidos.class.getName()).log(Level.SEVERE, null, ex);
        }
    
}//GEN-LAST:event_jButton1ActionPerformed

